class ToggleFriend{constructor(t){this.toggler=t,this.toggleFriend()}toggleFriend(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({url:$(e).attr("href")}).done((function(t){let l=$(e).children("input").attr("value");console.log(l),l=1==t.data.deleted?"REMOVE":"ADD",$(e).children("input").attr("value",l),$(e).children("input").html(`${l}`)})).fail((function(t){console.log("error in completing the request")}))}))}}